(function(){tinymce2.create("tinymce2.plugins.IESpell",{init:function(a,b){var c=this,d;if(!tinymce2.isIE){return}c.editor=a;a.addCommand("mceIESpell",function(){try{d=new ActiveXObject("ieSpell.ieSpellExtension");d.CheckDocumentNode(a.getDoc().documentElement)}catch(f){if(f.number==-2146827859){a.windowManager.confirm(a.getLang("iespell.download"),function(e){if(e){window.open("http://www.iespell.com/download.php","ieSpellDownload","")}})}else{a.windowManager.alert("Error Loading ieSpell: Exception "+f.number)}}});a.addButton("iespell",{title:"iespell.iespell_desc",cmd:"mceIESpell"})},getInfo:function(){return{longname:"IESpell (IE Only)",author:"Moxiecode Systems AB",authorurl:"http://tinymce2.moxiecode.com",infourl:"http://wiki.moxiecode.com/index.php/tinymce2:Plugins/iespell",version:tinymce2.majorVersion+"."+tinymce2.minorVersion}}});tinymce2.PluginManager.add("iespell",tinymce2.plugins.IESpell)})();